import React, { useState, useEffect, useRef } from 'react';
import styled from 'styled-components';

const OuterContainer = styled.div`
  width: 80%;
  padding: 20px;
  box-sizing: border-box;
  position: relative;
`;

const SliderContainer = styled.div`
  width: calc(100% - 40px);
  margin: auto;
  overflow: hidden;
  position: relative;
  border-radius: 15px;
`;

const SliderWrapper = styled.div`
  display: flex;
  transform: ${({ $activeIndex }) => `translateX(-${$activeIndex * 100}%)`};
  transition: ${({ $isTransitioning }) => ($isTransitioning ? 'transform 0.5s ease-in-out' : 'none')};
`;

const Slide = styled.div`
  min-width: 100%;
  height: 500px;
  background: center/cover no-repeat;
  border-radius: 15px;
  background-image: url(${props => props.image});
`;

const DotsContainer = styled.div`
  display: flex;
  justify-content: center;
  margin-top: 20px;
`;

const Dot = styled.div`
  width: 10px;
  height: 10px;
  margin: 0 5px;
  background-color: ${props => (props.$active ? '#00B5E2' : 'gray')};
  border-radius: 50%;
  cursor: pointer;
`;

const ImageSlider = ({ images }) => {
  const [activeIndex, setActiveIndex] = useState(1);
  const [isTransitioning, setIsTransitioning] = useState(false);
  const totalSlides = images.length;
  const transitionTimeoutRef = useRef(null);

  const handleTransitionEnd = () => {
    setIsTransitioning(false);
    if (activeIndex === 0) {
      setActiveIndex(totalSlides);
    } else if (activeIndex === totalSlides + 1) {
      setActiveIndex(1);
    }
  };

  useEffect(() => {
    const interval = setInterval(() => {
      setIsTransitioning(true);
      setActiveIndex((prevIndex) => prevIndex + 1);
    }, 3000);
    return () => clearInterval(interval);
  }, [totalSlides]);

  useEffect(() => {
    if (isTransitioning) {
      transitionTimeoutRef.current = setTimeout(() => {
        handleTransitionEnd();
      }, 500);
    }
    return () => clearTimeout(transitionTimeoutRef.current);
  }, [activeIndex, isTransitioning]);

  const goToSlide = (index) => {
    setIsTransitioning(true);
    setActiveIndex(index + 1);
  };

  const slides = [images[totalSlides - 1], ...images, images[0]];

  return (
    <OuterContainer>
      <SliderContainer>
        <SliderWrapper $activeIndex={activeIndex} $isTransitioning={isTransitioning}>
          {slides.map((image, index) => (
            <Slide key={index} image={image} />
          ))}
        </SliderWrapper>
      </SliderContainer>
      <DotsContainer>
        {images.map((_, index) => (
          <Dot
            key={index}
            $active={activeIndex === index + 1}
            onClick={() => goToSlide(index)}
          />
        ))}
      </DotsContainer>
    </OuterContainer>
  );
};

export default ImageSlider;
