// src/pages/Restaurants.js
import React from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import foodData from '../components/foodData';

const RestaurantsContainer = styled.div`
  font-family: Arial, sans-serif;
  padding: 20px;
`;

const Title = styled.h2`
  text-align: center;
`;

const RestaurantList = styled.div`
  display: flex;
  overflow-x: auto;
  white-space: nowrap;
  padding: 10px 0;
`;

const RestaurantCard = styled(Link)`
  border: 1px solid #e0e0e0;
  border-radius: 10px;
  overflow: hidden;
  margin: 0 15px;
  width: 300px;
  text-decoration: none;
  color: inherit;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  transition: transform 0.2s;
  flex: 0 0 auto;

  &:hover {
    transform: translateY(-5px);
  }
`;

const RestaurantImage = styled.img`
  width: 100%;
  height: 200px;
  object-fit: cover;
`;

const RestaurantContent = styled.div`
  padding: 15px;
`;

const RestaurantName = styled.h3`
  margin: 0;
  color: #333;
  font-size: 18px;
`;

const RestaurantDetails = styled.p`
  color: #757575;
  margin: 5px 0;
`;

const RestaurantLocation = styled.p`
  color: #757575;
  margin: 5px 0;
`;

function Restaurants() {
  return (
    <RestaurantsContainer>
      <Title>Restaurants</Title>
      <RestaurantList>
        {foodData.restaurants.map((restaurant, index) => (
          <RestaurantCard key={index} to={`/restaurant/${index}`}>
            <RestaurantImage src={restaurant.imageUrl} alt={restaurant.name} />
            <RestaurantContent>
              <RestaurantName>{restaurant.name}</RestaurantName>
              <RestaurantDetails>{restaurant.details}</RestaurantDetails>
              <RestaurantLocation>{restaurant.location}</RestaurantLocation>
            </RestaurantContent>
          </RestaurantCard>
        ))}
      </RestaurantList>
    </RestaurantsContainer>
  );
}

export default Restaurants;
