import React from 'react';
import styled from 'styled-components';
import { useCart } from '../hooks/useCart';

const CartContainer = styled.div`
  font-family: Arial, sans-serif;
  padding: 20px;
`;

const CartItem = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 10px 0;
  border-bottom: 1px solid #e0e0e0;
`;

const CartItemDetails = styled.div`
  display: flex;
  flex-direction: column;
`;

const CartItemName = styled.h3`
  margin: 0;
`;

const CartItemPrice = styled.p`
  margin: 5px 0;
  font-weight: bold;
`;

const QuantityContainer = styled.div`
  display: flex;
  align-items: center;
`;

const QuantityButton = styled.button`
  padding: 5px;
  background-color: #ff5a5f;
  color: white;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  margin: 0 5px;
`;

const RemoveButton = styled.button`
  padding: 5px;
  background-color: #ff5a5f;
  color: white;
  border: none;
  border-radius: 5px;
  cursor: pointer;
`;

const CheckoutButton = styled.button`
  padding: 15px;
  background-color: #ff5a5f;
  color: white;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  font-size: 16px;
  width: 100%;
  margin-top: 20px;
`;

const Summary = styled.div`
  text-align: right;
  margin-top: 20px;
`;

const SummaryItem = styled.p`
  margin: 5px 0;
  font-weight: bold;
`;

function Cart() {
  const { cart, removeFromCart, updateQuantity } = useCart();

  const subtotal = cart.reduce((total, item) => total + item.price * item.quantity, 0);
  const tax = subtotal * 0.18;
  const total = subtotal + tax;

  return (
    <CartContainer>
      <h1>Your Cart</h1>
      {cart.map((item, index) => (
        <CartItem key={index}>
          <CartItemDetails>
            <CartItemName>{item.name}</CartItemName>
            <CartItemPrice>₹{item.price}</CartItemPrice>
          </CartItemDetails>
          <QuantityContainer>
            <QuantityButton onClick={() => updateQuantity(item, item.quantity - 1)} disabled={item.quantity <= 1}>-</QuantityButton>
            <p>{item.quantity}</p>
            <QuantityButton onClick={() => updateQuantity(item, item.quantity + 1)}>+</QuantityButton>
          </QuantityContainer>
          <RemoveButton onClick={() => removeFromCart(index)}>Remove</RemoveButton>
        </CartItem>
      ))}
      <Summary>
        <SummaryItem>Subtotal: ₹{subtotal.toFixed(2)}</SummaryItem>
        <SummaryItem>Tax (18%): ₹{tax.toFixed(2)}</SummaryItem>
        <SummaryItem>Total: ₹{total.toFixed(2)}</SummaryItem>
      </Summary>
      <CheckoutButton>Proceed to Checkout</CheckoutButton>
    </CartContainer>
  );
}

export default Cart;
