import React from 'react';
import styled from 'styled-components';
import { useLocation } from 'react-router-dom';

const OrdersContainer = styled.div`
  font-family: Arial, sans-serif;
  padding: 20px;
  padding-left: 250px;
  padding-right: 250px;
`;

const OrderItem = styled.div`
  border: 1px solid #e0e0e0;
  border-radius: 10px;
  padding: 15px;
  margin-bottom: 15px;
  background-color: #f9f9f9;
`;

const OrderTitle = styled.h2`
  margin-top: 0;
`;

const OrderDetail = styled.p`
  margin: 5px 0;
`;

const FoodOrders = () => {
  const location = useLocation();
  const { orders } = location.state || { orders: [] };

  return (
    <OrdersContainer>
      <h1>Food Orders</h1>
      {orders.length === 0 ? (
        <p>No orders placed yet.</p>
      ) : (
        orders.map((order, index) => (
          <OrderItem key={index}>
            <OrderTitle>Order #{order.orderId}</OrderTitle>
            <OrderDetail>Address: {order.address}</OrderDetail>
            <OrderDetail>Time: {new Date(order.date).toLocaleString()}</OrderDetail>
            <OrderDetail>Total: ${order.total.toFixed(2)}</OrderDetail>
            <h3>Items:</h3>
            {order.cart.map((item, idx) => (
              <OrderDetail key={idx}>
                {item.quantity} x {item.name} (${item.price})
              </OrderDetail>
            ))}
          </OrderItem>
        ))
      )}
    </OrdersContainer>
  );
};

export default FoodOrders;
