// src/pages/Checkout.js
import React, { useState } from 'react';
import styled from 'styled-components';
import { useHistory } from 'react-router-dom';
import { useCart } from '../hooks/useCart';
import { useOrderHistory } from '../hooks/useOrderHistory';

const CheckoutContainer = styled.div`
  font-family: Arial, sans-serif;
  padding: 20px;
  max-width: 600px;
  margin: 0 auto;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
`;

const Label = styled.label`
  margin-bottom: 10px;
  font-weight: bold;
`;

const Input = styled.input`
  padding: 10px;
  margin-bottom: 20px;
  border: 1px solid #ccc;
  border-radius: 5px;
`;

const Button = styled.button`
  padding: 15px;
  background-color: #ff5a5f;
  color: white;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  font-size: 16px;
`;

const Summary = styled.div`
  margin-top: 20px;
`;

const SummaryItem = styled.p`
  margin: 5px 0;
  font-weight: bold;
`;

const Checkout = () => {
  const { cart, clearCart } = useCart();
  const { addOrder } = useOrderHistory();
  const [address, setAddress] = useState('');
  const history = useHistory();

  const subtotal = cart.reduce((total, item) => total + parseFloat(item.price.replace('$', '')) * item.quantity, 0);
  const tax = subtotal * 0.18;
  const total = subtotal + tax;

  const handleSubmit = (e) => {
    e.preventDefault();
    const order = {
      id: Date.now(),
      items: cart,
      total: total.toFixed(2),
      address,
      time: new Date().toLocaleString(),
    };
    addOrder(order);
    clearCart();
    history.push(`/order-confirmation/${order.id}`);
  };

  return (
    <CheckoutContainer>
      <h1>Checkout</h1>
      <Form onSubmit={handleSubmit}>
        <Label htmlFor="address">Enter your address:</Label>
        <Input
          type="text"
          id="address"
          value={address}
          onChange={(e) => setAddress(e.target.value)}
          required
        />
        <Button type="submit">Place Order</Button>
      </Form>
      <Summary>
        <SummaryItem>Subtotal: ${subtotal.toFixed(2)}</SummaryItem>
        <SummaryItem>Tax (18%): ${tax.toFixed(2)}</SummaryItem>
        <SummaryItem>Total: ${total.toFixed(2)}</SummaryItem>
      </Summary>
    </CheckoutContainer>
  );
};

export default Checkout;
