// src/pages/Restaurants.js
import React from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import foodData from '../components/foodData';
import { FaStar } from 'react-icons/fa';

const RestaurantsContainer = styled.div`
  font-family: Arial, sans-serif;
  padding: 20px;
`;

const RestaurantList = styled.div`
  display: flex;
  flex-wrap: wrap;
`;

const RestaurantCard = styled(Link)`
  border: 1px solid #e0e0e0;
  border-radius: 10px;
  overflow: hidden;
  margin: 15px;
  width: 300px;
  text-decoration: none;
  color: inherit;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  transition: transform 0.2s;

  &:hover {
    transform: translateY(-5px);
  }
`;

const ImageContainer = styled.div`
  position: relative;
  width: 100%;
  height: 200px;
`;

const RestaurantImage = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
`;

const DeliveryTimeOverlay = styled.div`
  position: absolute;
  top: 10px;
  left: 10px;
  background-color: rgba(0, 0, 0, 0.5);
  color: white;
  padding: 5px 10px;
  border-radius: 5px;
  font-size: 12px;
`;

const RestaurantContent = styled.div`
  padding: 15px;
`;

const RestaurantName = styled.h3`
  margin: 0;
  color: #333;
  font-size: 18px;
`;

const RestaurantDetails = styled.p`
  color: #757575;
  margin: 5px 0;
`;

const RestaurantLocation = styled.p`
  color: #757575;
  margin: 5px 0;
`;

const Rating = styled.div`
  font-size: 14px;
  color: #ff9900;
  margin: 5px 0;
  display: flex;
  align-items: center;
`;

const RatingValue = styled.span`
  margin-left: 5px;
  color: #333;
  font-size: 14px;
`;

function Restaurants() {
  return (
    <RestaurantsContainer>
      <h1>Restaurants</h1>
      <RestaurantList>
        {foodData.restaurants.map((restaurant) => (
          <RestaurantCard key={restaurant.id} to={`/restaurant/${restaurant.id}`}>
            <ImageContainer>
              <RestaurantImage src={restaurant.imageUrl} alt={restaurant.name} />
              <DeliveryTimeOverlay>{restaurant.time}</DeliveryTimeOverlay>
            </ImageContainer>
            <RestaurantContent>
              <RestaurantName>{restaurant.name}</RestaurantName>
              <RestaurantDetails>{restaurant.details}</RestaurantDetails>
              <RestaurantLocation>{restaurant.location}</RestaurantLocation>
              <Rating>
                <FaStar />
                <RatingValue>{restaurant.rating} ({restaurant.reviews} reviews)</RatingValue>
              </Rating>
            </RestaurantContent>
          </RestaurantCard>
        ))}
      </RestaurantList>
    </RestaurantsContainer>
  );
}

export default Restaurants;
