import React from 'react';
import styled from 'styled-components';
import { useLocation } from 'react-router-dom';

const OrderConfirmationContainer = styled.div`
  font-family: Arial, sans-serif;
  padding: 20px;
  padding-left: 250px;
  padding-right: 250px;
`;

const Summary = styled.div`
  text-align: left;
  margin-top: 20px;
`;

const SummaryItem = styled.p`
  margin: 5px 0;
  font-weight: bold;
`;

function OrderConfirmation() {
  const location = useLocation();
  const { orderId, address, cart, total } = location.state;

  return (
    <OrderConfirmationContainer>
      <h1>Order Confirmation</h1>
      <p>Thank you for your order! Your order number is {orderId}.</p>
      <p>It will be delivered to the following address:</p>
      <p>{address}</p>
      <Summary>
        <h2>Order Summary</h2>
        {cart.map((item, index) => (
          <SummaryItem key={index}>
            {item.quantity} x {item.name} (${item.price})
          </SummaryItem>
        ))}
        <SummaryItem>Total: ${total.toFixed(2)}</SummaryItem>
      </Summary>
    </OrderConfirmationContainer>
  );
}

export default OrderConfirmation;
