import React from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import { FaHeart, FaStar } from 'react-icons/fa';

const Card = styled.div`
  border: 1px solid #e0e0e0;
  border-radius: 10px;
  overflow: hidden;
  margin: 15px;
  width: 300px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  transition: transform 0.2s;
  position: relative;

  &:hover {
    transform: translateY(-5px);
  }
`;

const Image = styled.img`
  width: 100%;
  height: 200px;
  object-fit: cover;
`;

const Content = styled.div`
  padding: 15px;
  text-align: left;
`;

const Title = styled.h3`
  margin: 0;
  color: #333;
  font-size: 18px;
`;

const Location = styled.p`
  color: #757575;
  margin: 5px 0;
`;

const Price = styled.p`
  color: #333;
  font-weight: bold;
  margin: 5px 0;
`;

const Host = styled.p`
  color: #757575;
  margin: 5px 0;
`;

const Rating = styled.div`
  display: flex;
  align-items: center;
  color: #ff9900;
  margin-top: 5px;
`;

const RatingValue = styled.span`
  margin-left: 5px;
  color: #333;
  font-size: 16px;
`;

const HeartButton = styled.button`
  position: absolute;
  top: 10px;
  right: 10px;
  background: none;
  border: none;
  color: #ff5a5f;
  cursor: pointer;
  font-size: 24px;
`;

const StyledLink = styled(Link)`
  text-decoration: none;
  color: inherit;
`;

const PlaceCard = ({ place }) => (
  <StyledLink to={`/place/${place.id}`}>
    <Card>
      <HeartButton>
        <FaHeart />
      </HeartButton>
      <Image src={place.imageUrl} alt={place.name} />
      <Content>
        <Title>{place.name}</Title>
        <Location>{place.location}</Location>
        <Price>â‚¹{place.price} per guest</Price>
        <Host>Hosted by {place.host}</Host>
        <Rating>
          <FaStar />
          <RatingValue>{place.rating}</RatingValue>
        </Rating>
      </Content>
    </Card>
  </StyledLink>
);

export default PlaceCard;
