import React, { useEffect } from 'react';
import styled from 'styled-components';
import { useNavigate, useLocation } from 'react-router-dom';
import { useCart } from '../hooks/useCart';

const ConfirmationContainer = styled.div`
  font-family: Arial, sans-serif;
  padding: 20px;
  padding-left: 250px;
  padding-right: 250px;
`;

const OrderDetail = styled.p`
  margin: 5px 0;
  font-weight: bold;
`;

const OrderConfirmation = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { orderId, address, cart, total } = location.state;
  const { clearCart } = useCart();
  const orderDate = new Date();

  const order = {
    orderId,
    address,
    date: orderDate,
    total,
    cart
  };

  useEffect(() => {
    // Save the order to the order history.
    const orders = JSON.parse(localStorage.getItem('orders')) || [];
    orders.push(order);
    localStorage.setItem('orders', JSON.stringify(orders));

    // Clear the cart after placing the order
    clearCart();
  }, [order, clearCart]);

  return (
    <ConfirmationContainer>
      <h1>Order Confirmation</h1>
      <OrderDetail>Order ID: {orderId}</OrderDetail>
      <OrderDetail>Address: {address}</OrderDetail>
      <OrderDetail>Order Date: {orderDate.toLocaleString()}</OrderDetail>
      <OrderDetail>Total: ${total.toFixed(2)}</OrderDetail>
      <h3>Items:</h3>
      {cart.map((item, index) => (
        <OrderDetail key={index}>
          {item.quantity} x {item.name} (${item.price})
        </OrderDetail>
      ))}
      <button onClick={() => navigate('/food-orders')}>View Orders</button>
    </ConfirmationContainer>
  );
};

export default OrderConfirmation;
