import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import { FaHeart, FaRegHeart, FaStar } from 'react-icons/fa';

const Card = styled.div`
  border: 1px solid #e0e0e0;
  border-radius: 10px;
  overflow: hidden;
  margin: 15px;
  width: 300px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  transition: transform 0.2s;
  position: relative;

  &:hover {
    transform: translateY(-5px);
  }
`;

const ImageContainer = styled.div`
  position: relative;
`;

const Image = styled.img`
  width: 100%;
  height: 200px;
  object-fit: cover;
`;

const FavoriteButton = styled.button`
  position: absolute;
  top: 10px;
  right: 10px;
  background: none;
  border: none;
  color: ${props => (props.isFavorite ? '#ff5a5f' : '#ffffff')};
  cursor: pointer;
  font-size: 24px;
`;

const Content = styled.div`
  padding: 15px;
  text-align: left;
`;

const Title = styled.h3`
  margin: 0;
  color: #333;
  font-size: 18px;
`;

const Location = styled.p`
  color: #757575;
  margin: 5px 0;
`;

const Details = styled.p`
  color: #757575;
  margin: 5px 0;
`;

const Price = styled.p`
  color: #333;
  font-weight: bold;
  margin: 5px 0;
`;

const Host = styled.p`
  color: #757575;
  margin: 5px 0;
`;

const Rating = styled.div`
  display: flex;
  align-items: center;
  justify-content: flex-end;
  color: #ff9900;
  margin-top: 5px;
`;

const RatingValue = styled.span`
  margin-left: 5px;
  color: #333;
  font-size: 16px;
`;

const StyledLink = styled(Link)`
  text-decoration: none;
  color: inherit;
`;

const haversineDistance = (lat1, lon1, lat2, lon2) => {
  function toRad(x) {
    return x * Math.PI / 180;
  }

  const R = 6371; // Radius of Earth in kilometers
  const dLat = toRad(lat2 - lat1);
  const dLon = toRad(lon2 - lon1);
  const a = 
    Math.sin(dLat / 2) * Math.sin(dLat / 2) +
    Math.cos(toRad(lat1)) * Math.cos(toRad(lat2)) *
    Math.sin(dLon / 2) * Math.sin(dLon / 2);
  const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
  return R * c;
};

const PlaceCard = ({ place }) => {
  const [isFavorite, setIsFavorite] = useState(false);
  const [distance, setDistance] = useState(null);
  const [convertedPrice, setConvertedPrice] = useState(place.price);
  const [currencySymbol, setCurrencySymbol] = useState('₹');

  useEffect(() => {
    const getCurrencySymbol = (currencyCode) => {
      const currencySymbols = {
        USD: '$',
        EUR: '€',
        GBP: '£',
        INR: '₹',
        JPY: '¥',
        // Add more currency symbols as needed
      };
      return currencySymbols[currencyCode] || currencyCode;
    };

    const fetchCurrencyConversion = async (baseCurrency, targetCurrency) => {
      try {
        const response = await fetch(`https://api.exchangerate-api.com/v4/latest/${baseCurrency}`);
        const data = await response.json();
        const exchangeRate = data.rates[targetCurrency];
        return exchangeRate;
      } catch (error) {
        console.error('Error fetching currency conversion:', error);
        return 1;
      }
    };

    const updateLocationAndCurrency = async () => {
      if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(
          async (position) => {
            const { latitude, longitude } = position.coords;
            const placeLatitude = place.details.latitude; // Assuming place object has latitude
            const placeLongitude = place.details.longitude; // Assuming place object has longitude

            // Calculate distance if lat and lon are available
            if (placeLatitude && placeLongitude) {
              const calculatedDistance = haversineDistance(latitude, longitude, placeLatitude, placeLongitude);
              setDistance(calculatedDistance.toFixed(2));
            }

            // Fetch user's country and currency
            const locationResponse = await fetch(`https://nominatim.openstreetmap.org/reverse?format=jsonv2&lat=${latitude}&lon=${longitude}`);
            const locationData = await locationResponse.json();
            const country = locationData.address.country || 'Unknown Country';
            const userCurrency = country === 'United States' ? 'USD' : 'INR'; // Replace with actual logic to determine currency
            setCurrencySymbol(getCurrencySymbol(userCurrency));

            // Convert price if needed
            const placeCurrency = 'USD'; // Replace with the actual currency of the place
            if (userCurrency !== placeCurrency) {
              const exchangeRate = await fetchCurrencyConversion(placeCurrency, userCurrency);
              const convertedPrice = place.price * exchangeRate;
              setConvertedPrice(convertedPrice.toFixed(2));
            }
          },
          (error) => {
            console.error(error);
          }
        );
      }
    };

    updateLocationAndCurrency();
  }, [place]);

  const toggleFavorite = (e) => {
    e.stopPropagation();
    e.preventDefault();
    setIsFavorite(!isFavorite);
  };

  return (
    <StyledLink to={`/place/${place.id}`}>
      <Card>
        <ImageContainer>
          <Image src={place.imageUrl} alt={place.name} />
          <FavoriteButton onClick={toggleFavorite} isFavorite={isFavorite}>
            {isFavorite ? <FaHeart /> : <FaRegHeart />}
          </FavoriteButton>
        </ImageContainer>
        <Content>
          <Title>{place.name}</Title>
          <Location>{place.location}</Location>
          {distance && <Details>{distance} kilometres away</Details>}
          <Details>{place.dates}</Details>
          <Price>{currencySymbol}{convertedPrice} total before taxes</Price>
          <Host>Hosted by {place.host}</Host>
          <Rating>
            <FaStar />
            <RatingValue>{place.rating}</RatingValue>
          </Rating>
        </Content>
      </Card>
    </StyledLink>
  );
};

export default PlaceCard;
