import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { ReactComponent as LocationIcon } from '../images/location.svg'; // Adjust the path as necessary

const Button = styled.button`
  display: flex;
  align-items: center;
  border: 1px solid lightgrey;
  background-color: white;
  border-radius: 25px;
  padding: 10px 10px;
  margin-right: 30px; /* Add margin to separate it from the search bar */
  cursor: pointer;
  transition: border-color 0.3s ease, background-color 0.3s ease;

  &:hover {
    background-color: #f0f0f0;
    border-color: blue;
  }

  &:active {
    border-color: blue;
  }
`;

const Icon = styled(LocationIcon)`
  width: 24px;
  height: 24px;
  margin-right: 10px;
`;

const LocationText = styled.span`
  font-size: 1rem;
  color: black;
  margin-right: 10px;
`;

const LocationButton = () => {
  const [currentLocation, setCurrentLocation] = useState('Loading...');

  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          fetch(`https://nominatim.openstreetmap.org/reverse?format=jsonv2&lat=${latitude}&lon=${longitude}`)
            .then((response) => response.json())
            .then((data) => {
              const { address } = data;
              const city = address.city || address.town || address.village || address.state_district || address.county || 'Unknown City';
              const country = address.country || 'Unknown Country';
              const location = `${city}, ${country}`;

              // Truncate the location if it's too long
              const truncatedLocation = location.length > 20 ? `${location.substring(0, 20)}...` : location;

              setCurrentLocation(truncatedLocation);
            })
            .catch((error) => {
              console.error(error);
              setCurrentLocation('Unable to retrieve location');
            });
        },
        (error) => {
          console.error(error);
          setCurrentLocation('Unable to retrieve location');
        }
      );
    } else {
      setCurrentLocation('Geolocation not supported');
    }
  }, []);

  return (
    <>
      <Button>
        <Icon />
        <LocationText>{currentLocation}</LocationText>
      </Button>
    </>
  );
};

export default LocationButton;
