// src/utils/currency.js
export const getCurrencySymbol = (location) => {
    switch (location) {
      case 'India':
        return '₹';
      case 'Europe':
        return '€';
      case 'UK':
        return '£';
      default:
        return '$';
    }
  };

  export const convertPrice = (price, location) => {
    switch (location) {
      case 'India':
        return price * 75; // Example conversion rate
      case 'Europe':
        return price * 0.85; // Example conversion rate
      case 'UK':
        return price * 0.75; // Example conversion rate
      default:
        return price;
    }
  };
