// src/components/InspirationComponent.js
import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import foodData from './foodData';
import { useCart } from '../hooks/useCart';

const InspirationContainer = styled.div`
  padding: 20px;
`;

const Title = styled.h2`
  text-align: center;
`;

const InspirationList = styled.div`
  display: flex;
  justify-content: space-around;
  flex-wrap: wrap;
`;

const InspirationItem = styled.div`
  text-align: center;
  border: 1px solid #e0e0e0;
  border-radius: 10px;
  overflow: hidden;
  margin: 15px;
  width: 200px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
`;

const InspirationImage = styled.img`
  width: 100%;
  height: 150px;
  object-fit: cover;
`;

const Details = styled.div`
  padding: 10px;
`;

const AddToCartButton = styled.button`
  padding: 5px 10px;
  background-color: #ff5a5f;
  color: white;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  margin-top: 10px;
  margin-bottom: 10px;
`;

const InspirationComponent = () => {
  const { addToCart } = useCart();
  const [shuffledFoodItems, setShuffledFoodItems] = useState([]);

  useEffect(() => {
    const foodItems = foodData.restaurants.flatMap(restaurant =>
      restaurant.menu.map((item, index) => ({
        ...item,
        restaurantName: restaurant.name,
        restaurantDetails: restaurant.details,
        restaurantLocation: restaurant.location,
        restaurantRating: restaurant.rating,
        restaurantReviews: restaurant.reviews,
        uniqueId: `${restaurant.id}-${index}`, // Create a unique identifier
        deliveryTime: restaurant.time
      }))
    );
    const shuffled = foodItems.sort(() => 0.5 - Math.random()).slice(0, 10);
    setShuffledFoodItems(shuffled);
  }, []);

  const handleAddToCart = (item) => {
    addToCart(item);
    setShuffledFoodItems((prevItems) =>
      prevItems.map((foodItem) =>
        foodItem.uniqueId === item.uniqueId
          ? { ...foodItem, added: true }
          : foodItem
      )
    );
  };

  return (
    <InspirationContainer>
      <Title>Inspiration for your first order</Title>
      <InspirationList>
        {shuffledFoodItems.map((item, index) => (
          <InspirationItem key={index}>
            <InspirationImage src={item.imageUrl} alt={item.name} />
            <Details>
              <p>{item.name}</p>
              <p>{item.restaurantName}</p>
              <p>Price: {item.price}</p>
              <p>Delivery Time: {item.deliveryTime}</p>
              <p>Rating: {item.restaurantRating} ({item.restaurantReviews} reviews)</p>
              <AddToCartButton
                onClick={() => handleAddToCart(item)}
                disabled={item.added}
              >
                {item.added ? 'Added to Cart' : 'Add to Cart'}
              </AddToCartButton>
            </Details>
          </InspirationItem>
        ))}
      </InspirationList>
    </InspirationContainer>
  );
};

export default InspirationComponent;
