import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import placesData from '../data/places';

const Container = styled.div`
  padding: 20px;
  background-color: #f5f5f5;
`;

const MainImage = styled.img`
  grid-area: main;
  width: 100%;
  height: auto;
`;

const GridImage = styled.img`
  width: 100%;
  height: auto;
`;

const ImageGrid = styled.div`
  display: grid;
  grid-template-areas:
    "main main side1 side2"
    "main main side3 side4";
  grid-template-columns: 2fr 1fr 1fr 1fr;
  grid-template-rows: repeat(2, auto);
  grid-gap: 10px;
  margin-bottom: 20px;

  @media (max-width: 768px) {
    grid-template-areas:
      "main"
      "side1"
      "side2"
      "side3"
      "side4";
    grid-template-columns: 1fr;
  }
`;

const ShowMoreButton = styled.button`
  display: block;
  margin: 10px auto;
  padding: 10px 20px;
  background-color: #ff5a5f;
  color: white;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  font-size: 16px;
`;

const Title = styled.h1`
  color: #333;
`;

const Subtitle = styled.h2`
  color: #555;
`;

const Section = styled.section`
  margin: 20px 0;
`;

const HostInfo = styled.div`
  display: flex;
  align-items: center;
`;

const HostImage = styled.img`
  border-radius: 50%;
  width: 50px;
  height: 50px;
  margin-right: 20px;
`;

const AmenitiesList = styled.ul`
  list-style-type: none;
  padding: 0;
`;

const Amenity = styled.li`
  padding: 5px 0;
`;

const ReviewsList = styled.ul`
  list-style-type: none;
  padding: 0;
`;

const Review = styled.li`
  padding: 10px 0;
`;

const BookingSection = styled.section`
  background-color: #fff;
  padding: 20px;
  border-radius: 8px;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
`;

const Price = styled.p`
  color: #333;
  font-weight: bold;
  font-size: 24px;
  margin-bottom: 20px;
`;

const Label = styled.label`
  display: block;
  margin-bottom: 5px;
  color: #555;
`;

const Input = styled.input`
  width: 100%;
  padding: 10px;
  margin-bottom: 10px;
  border: 1px solid #ccc;
  border-radius: 4px;
`;

const Button = styled.button`
  padding: 10px 20px;
  background-color: #ff5a5f;
  color: white;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  font-size: 16px;
`;

const PlaceDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const place = placesData.find((place) => place.id.toString() === id);

  const [startDate, setStartDate] = useState(null);
  const [endDate, setEndDate] = useState(null);
  const [guests, setGuests] = useState(1);
  const [total, setTotal] = useState(0);
  const [showMore, setShowMore] = useState(false);

  const handleDateChange = (dates) => {
    const [start, end] = dates;
    setStartDate(start);
    setEndDate(end);

    if (start && end) {
      const days = Math.ceil((end - start) / (1000 * 60 * 60 * 24));
      setTotal(days * place.price);
    }
  };

  const handleGuestsChange = (e) => {
    setGuests(e.target.value);
  };

  const handleBook = () => {
    if (startDate && endDate) {
      navigate('/booking-confirmation', {
        state: { startDate, endDate, guests, place, total }
      });
    } else {
      alert("Please select valid dates.");
    }
  };

  const toggleShowMore = () => {
    setShowMore(!showMore);
  };

  if (!place) {
    return <Container>Place not found</Container>;
  }

  return (
    <Container>
      <ImageGrid>
        <MainImage src={place.details.images[0]} alt={`${place.name} main`} />
        {place.details.images.slice(1, 5).map((img, index) => (
          <GridImage key={index} src={img} alt={`${place.name} ${index + 1}`} style={{ gridArea: `side${index + 1}` }} />
        ))}
      </ImageGrid>
      {showMore && (
        <ImageGrid>
          {place.details.images.slice(5).map((img, index) => (
            <GridImage key={index + 5} src={img} alt={`${place.name} ${index + 6}`} />
          ))}
        </ImageGrid>
      )}
      {!showMore && place.details.images.length > 5 && (
        <ShowMoreButton onClick={toggleShowMore}>
          {showMore ? "Show Less" : "Show More"}
        </ShowMoreButton>
      )}
      <Title>{place.name}</Title>
      <Subtitle>Hosted by {place.host}</Subtitle>
      <Section>
        <p>{place.details.description}</p>
      </Section>
      <Section>
        <h3>Hosted by</h3>
        <HostInfo>
          <HostImage src={place.hostDetails.imageUrl} alt={place.hostDetails.name} />
          <div>
            <p>{place.hostDetails.name}</p>
            <p>Superhost: {place.hostDetails.superhost ? "Yes" : "No"}</p>
            <p>Response rate: {place.hostDetails.responseRate}</p>
            <p>Languages: {place.hostDetails.languages.join(", ")}</p>
          </div>
        </HostInfo>
        <p>{place.hostDetails.description}</p>
      </Section>
      <Section>
        <h3>Amenities</h3>
        <AmenitiesList>
          {place.details.amenities.map((amenity, index) => (
            <Amenity key={index}>{amenity}</Amenity>
          ))}
        </AmenitiesList>
      </Section>
      <Section>
        <h3>Reviews</h3>
        <ReviewsList>
          {place.details.reviews.map((review) => (
            <Review key={review.id}>
              <p>{review.name}</p>
              <p>{review.comment}</p>
              <p>Rating: {review.rating}</p>
            </Review>
          ))}
        </ReviewsList>
      </Section>
      <BookingSection>
        <Price>₹{place.price} per night</Price>
        <Label htmlFor="dates">Select Dates</Label>
        <DatePicker
          selected={startDate}
          onChange={handleDateChange}
          startDate={startDate}
          endDate={endDate}
          selectsRange
          inline
        />
        <Label htmlFor="guests">Number of Guests</Label>
        <Input
          type="number"
          id="guests"
          value={guests}
          min="1"
          onChange={handleGuestsChange}
        />
        <Button onClick={handleBook}>Book Now</Button>
        {total > 0 && (
          <p>Total: ₹{total}</p>
        )}
      </BookingSection>
    </Container>
  );
};

export default PlaceDetail;
